from typing import Dict, Optional

from langchain_community.embeddings.fastembed import FastEmbedEmbeddings

from langflow.custom import CustomComponent


class FastEmbedEmbeddingsComponent(CustomComponent):
    display_name = "FastEmbed Embeddings"
    description = "Generate embeddings using FastEmbed models."
    documentation = (
        "https://python.langchain.com/docs/modules/data_connection/text_embedding/integrations/fastembed"
    )
    icon = "embeddings"

    def build_config(self):
        return {
            "model_name": {"display_name": "Model Name"}
        }

    def build(
        self,
        model_name: str = "BAAI/bge-base-en-v1.5"
    ) -> FastEmbedEmbeddings:
        return FastEmbedEmbeddings(
            model_name=model_name,
        )
